CREATE VIEW `kynaforkids-server-production.kynaforkids.Test_student_booking`
AS SELECT 
  bs.teacher_id as TeacherID,
  if(bs.rating = 2,1,0) as total_dat,
  if(bs.rating = 1,1,0)  as total_khongdat,
  if(bs.rating = 3,1,0) as total_duoichuan,
  if(bs.rating = 4,1,0) as total_nopay,
  if(bs.rating = 4 and bs.program_id = 8,1,0) as total_nopay_1_ca,
  if(bs.rating = 4 and bs.program_id = 13,1,0) as total_nopay_2_ca,
  date(bs.begin_at) as begin_at
FROM `kynaforkids-server-production.kynaforkids.booking_session_student` bss
INNER JOIN `kynaforkids-server-production.kynaforkids.booking_session_view` bs ON bs.id = bss.booking_session_id
WHERE bs.status_id IN (5)
AND bs.program_id IN (8,13,47,48,53,54,55,56,89,90)
;

